{ # se déplacer dans le bon dossier
if [ -d '/mnt/pi/demetrius/download_center/series_tv/' ];then
	cd /mnt/pi/demetrius/download_center/series_tv/ && echo -e "\nVoici la liste des séries présentes sur le disque :" && ls -l && echo -e "\nA quelle série appartiennent les épisodes que vous voulez renommer et classer par saisons ?" && read NomSerie
			
