{ # selectionner l'extension des fichiers à traiter

echo -e "\nQuelle est l'extension des fichiers à renommer ?"
read Extension

chemin=/mnt/pi/demetrius/download_center/series_tv/$NomSerie/

nbfiles=$(find $chemin -maxdepth 1 -name '*.'$Extension | wc -l)

if [ "$nbfiles" != "0" ];then
	echo -e "\nIl y a '$nbfiles' fichiers contenant cette extension dans le dossier '$NomSerie'." && ls -l *.$Extension
else
	echo -e "\nCette extension n'est pas présente, veuillez vérifier son orthographe et la saisir à nouveau :" && read Extension
	nbfiles=$(find $chemin -maxdepth 1 -name '*.'$Extension | wc -l)
	if [ "$nbfiles" != "0" ];then
		echo -e "\nIl y a '$nbfiles' fichiers contenant cette extension dans le dossier '$NomSerie'." && ls -l *.$Extension
	else
		echo -e "\nCette extension n'est pas présente, veuillez vérifier son orthographe et la saisir à nouveau :" && read Extension
